import React, { PureComponent } from 'react'
import CustomNavBar from './NavBar';
import DatePickers from './DatePickers';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Chart from './Chart';
import { dataService } from '../services/dataService';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));
class MainView extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            data: []
        }
        this.setData = this.setData.bind(this)
    }

    componentDidMount() {
    }

    setData(webData) {
        this.setState({data: webData})
    }


    render() {
        const classes = this.props.classes;
        return (
            <div>
                <CustomNavBar />
                <div className={classes.root}>
                    <Grid container spacing={3} style={{ padding: 20 }} justify="center">
                        <Grid item xs={6} style={{ padding: 20 }} >
                            <Paper className={classes.paper}><DatePickers callbackSetData={this.setData}/></Paper>
                        </Grid>
                    </Grid>
                    <Grid container spacing={3} style={{ padding: 20 }} justify="center">
                        <Grid item xs={12}>
                            <Paper className={classes.paper}><Chart data={this.state.data}/></Paper>
                        </Grid>
                    </Grid>
                </div>
            </div>

        )
    }
}


export default withStyles(useStyles)(MainView);
